workflow-item.script:
var output = "";

output = output + ("## Workflow: " + workflow.name + "\n");
output = output + ("Name: " + workflow.name + "\n");
output = output + ("Location: " + workflow.workflowCategory.path + "\n");
output = output + ("Description: " + workflow.description + "\n");
output = output + ("Version: " + workflow.version + "\n");
output = output + ("ID: " + workflow.id + "\n");
output = output + ("### Inputs" + "\n");
if(workflow.inParameters.length > 0){
    output = output + ("| Name | Type | Description |" + "\n");
    output = output + ("| ---- | ---- | ----------- |" + "\n");
    for(i=0;i<workflow.inParameters.length;i++){
        var inName = workflow.inParameters[i].name;
        var inType = workflow.inParameters[i].type;
        var inDesc = workflow.inParameters[i].description;
        output = output + ("| " + inName + " | " + inType + " | " + inDesc + " |" + "\n");
    }
}
else{
    output = output + ("This workflow has no inputs." + "\n");
}
output = output + ("### Outputs" + "\n");
if(workflow.outParameters.length > 0){
    output = output + ("| Name | Type | Description |" + "\n");
    output = output + ("| ---- | ---- | ----------- |" + "\n");
    for(i=0;i<workflow.outParameters.length;i++){
        var outName = workflow.outParameters[i].name;
        var outType = workflow.outParameters[i].type;
        var outDesc = workflow.outParameters[i].description;
        output = output + ("| " + outName + " | " + outType + " | " + outDesc + " |" + "\n");
    }
}
else{
    output = output + ("This workflow has no outputs." + "\n");
}
output = output + ("### Variables" + "\n");
if(workflow.attributes.length > 0){
    output = output + ("| Name | Type | Description |" + "\n");
    output = output + ("| ---- | ---- | ----------- |" + "\n");
    for(i=0;i<workflow.attributes.length;i++){
        var attrName = workflow.attributes[i].name;
        var attrType = workflow.attributes[i].type;
        var attrDesc = workflow.attributes[i].description;
        output = output + ("| " + attrName + " | " + attrType + " | " + attrDesc + " |" + "\n");
    }
}
else{
    output = output + ("This workflow has no variables." + "\n");
}
output = output + ("### Notes" + "\n");
output = output + ("Nothing notable to report about this workflow." + "\n");

System.log(output);
